public class ConditionalStatement1{
	public static void main(String args[]) {
		char ch='B';
		if(ch=='A'||ch=='E'||ch=='I'||ch=='O'||ch=='U') {
			System.out.println(ch+" is Vowel");
		}
		else {
			System.out.println(ch+" is Consonant");
		}
	}
}