// 3.WAP to print Student name, college name, student age, student percentage,student yop, student contact.

public class Variables{
	public static void main(String args[]) {
		String sName="Mantesh Hugar", cName="BKIT";
		int sAge=25;
		float sPer=67.55f;
		short yop=2019;
		long sContact=9876543210l;
		
		System.out.println("Student name "+sName);
		System.out.println("Student college name "+cName);
		System.out.println("Student age "+sAge);
		System.out.println("Student percentage "+sPer);
		System.out.println("Student Year of passes out "+yop);
		System.out.println("Student contact "+sContact);
	}
}